import React, { useState, useEffect } from 'react';
import axios from 'axios';
import EdittableTable from './EdittableTable';

const Table = () => {

    const [data, setData] = useState([]);

    const [addPost, setAddPost] = useState({
        title: '',
        description: '',
        deadline: '',
        priority: '',
    });

    const [editEntry, setEditEntry] = useState(null);

    const [editFormData, setEditFormData] = useState({
        title: '',
        description: '',
        deadline: '',
        priority: '',
    });

    const handleChange = (input) => (e) => {
        e.preventDefault();
        console.log(addPost);
        setAddPost({ ...addPost, [input]: e.target.value });
    };

    const handleAddPost = (e) => {
        e.preventDefault();

        const newPost = {
            title: addPost.title,
            description: addPost.description,
            deadline: addPost.deadline,
            priority: addPost.priority
        };

        const newPosts = [...data, newPost];
        setData(newPosts);
    };

    
    // Updating Values
    const handleUpdate = (input) => (e) => {
        e.preventDefault();
        setEditFormData({...editFormData, [input]: e.target.value});
    };

    const handleEdit = (e, data) => {
        e.preventDefault();

        setEditEntry(data.title);

        const values = {
            title: data.title,
            description: data.description,
            deadline: data.deadline,
            priority: data.priority
        }

        setEditFormData(values);
    };


    const fetchUrl = "https://jsonplaceholder.typicode.com/posts";
    useEffect(() => {
        async function fetchData() {
            const info = await axios.get(fetchUrl);
            setData(info.data)
        }
        fetchData();
    }, [fetchUrl]);
    console.log(data);

    return (
        <div>
            <div className="d-flex flex-row">
                <button type="button" className="me-3 btn btn-primary ml-auto d-block mb-2" data-bs-toggle="modal" data-bs-target="#addModalForm">
                    ADD
                </button>
            </div>
            <table className="table table-bordered border-primary table-responsive">
                <thead>
                    <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Deadline</th>
                        <th scope="col">Priority</th>
                        <th scope="col">Is Complete</th>
                        <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                    <EdittableTable 
                        data={data}
                        handleEdit={handleEdit}
                    />
                </tbody>
            </table>

            {/* Add Modal Form */}
            <div className="modal fade" id="addModalForm" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Add</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleAddPost}>
                                <div className="mb-3">
                                    <label className="form-label">Title</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="title"
                                        required
                                        onChange={handleUpdate("title")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Description</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="description"
                                        placeholder="description"
                                        required
                                        onChange={handleUpdate("description")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Deadline</label>
                                    <textarea
                                        rows="4"
                                        cols="50"
                                        className="form-control"
                                        name="deadline"
                                        placeholder="deadline"
                                        required
                                        onChange={handleUpdate("deadline")}
                                    ></textarea>
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Priority</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="priority"
                                        placeholder="priority"
                                        required
                                        onChange={handleUpdate("priority")}
                                    />
                                </div>
                                <div className="modal-footer d-block">
                                    <button type="submit" data-bs-dismiss="modal" className="btn btn-warning float-end">Submit</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>


            {/* Edit Modal Form */}
            <div className="modal fade" id="editModalForm" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Add</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleAddPost}>
                                <div className="mb-3">
                                    <label className="form-label">Title</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="title"
                                        required
                                        onChange={handleChange("title")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Description</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="description"
                                        placeholder="description"
                                        required
                                        onChange={handleChange("description")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Deadline</label>
                                    <textarea
                                        rows="4"
                                        cols="50"
                                        className="form-control"
                                        name="deadline"
                                        placeholder="deadline"
                                        required
                                        onChange={handleChange("deadline")}
                                    ></textarea>
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Priority</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="priority"
                                        placeholder="priority"
                                        required
                                        onChange={handleChange("priority")}
                                    />
                                </div>
                                <div className="modal-footer d-block">
                                    <button type="submit" data-bs-dismiss="modal" className="btn btn-warning float-end">Submit</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>


        </div>
    )
}

export default Table;
