import React, { useState } from 'react';
import EdittableTable from './EdittableTable';
import $, { data } from 'jquery';
import toastr from 'toastr'

const Table = () => {

    const [datas, setDatas] = useState([]);

    const [addPost, setAddPost] = useState({
        title: '',
        description: '',
        deadline: '',
        priority: '',
    });

    // Get ID
    const [editEntry, setEditEntry] = useState(null);

    const [editFormData, setEditFormData] = useState({
        title: '',
        description: '',
        deadline: '',
        priority: '',
    });

    // Receive values
    const handleChange = (input) => (e) => {
        e.preventDefault();
        console.log(addPost);
        setAddPost({ ...addPost, [input]: e.target.value });
    };

    // Add Data
    const handleAddPost = (e) => {
        e.preventDefault();

        if (!datas.find((data) => data.title === addPost.title)) {

            const newPost = {
                title: addPost.title,
                description: addPost.description,
                deadline: addPost.deadline,
                priority: addPost.priority
            };

            const newPosts = [...datas, newPost];
            setDatas(newPosts);
            toastr.success('Entry added succesfully', 'Success!');
            setAddPost(null);
        }
    };


    // Updating Values
    const handleUpdate = (input) => (e) => {
        e.preventDefault();
        setEditFormData({ ...editFormData, [input]: e.target.value });
    };

    // Handles putting the original values into the modal
    const handleEdit = (e, data) => {
        e.preventDefault();
        setEditEntry(data.title);
        console.log(data.title);

        const values = {
            title: data.title,
            description: data.description,
            deadline: data.deadline,
            priority: data.priority
        }

        setEditFormData(values);
    };

    // Saves the new updated values
    const handleSave = (e) => {
        e.preventDefault();

        const saveForm = {
            title: editEntry,
            description: editFormData.description,
            deadline: editFormData.deadline,
            priority: editFormData.priority,
        };

        const newForms = [...datas];

        const index = datas.findIndex((data) => data.title === editEntry);
        newForms[index] = saveForm;

        setDatas(newForms);
        setEditEntry(null);
        toastr.success('Entry updated succesfully', 'Success!');
    };


    // Deletes the entry from the table
    const handleDelete = (e, data) => {
        e.preventDefault();

        const newPosts = [...datas];
        const index = datas.findIndex((data) => data.title === editEntry);

        newPosts.splice(index, 1);

        setDatas(newPosts);
    };

    //$('#addModalForm').on('hidden.bs.modal', function(e) {
    //    $(this).find('#form')[0].reset();
    //});




    return (
        <div>
            <div className="d-flex flex-row">
                <button type="button" className="me-3 btn btn-primary ml-auto d-block mb-2" data-bs-toggle="modal" data-bs-target="#addModalForm">
                    ADD
                </button>
            </div>
            <table className="table table-bordered border-primary table-responsive">
                <thead>
                    <tr>
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Deadline</th>
                        <th scope="col">Priority</th>
                        <th scope="col">Is Complete</th>
                        <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                    <EdittableTable
                        datas={datas}
                        handleEdit={handleEdit}
                        handleDelete={handleDelete}
                    />
                </tbody>
            </table>

            {/* Add Modal Form */}
            <div className="modal fade" id="addModalForm" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Add</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleAddPost} name="form" id="form" className="needs-validation" novalidate>
                                <div className="mb-3">
                                    <label className="form-label">Title</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="title"
                                        required
                                        onChange={handleChange("title")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Description</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="description"
                                        placeholder="description"
                                        required
                                        onChange={handleChange("description")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Deadline</label>
                                    <textarea
                                        rows="4"
                                        cols="50"
                                        className="form-control"
                                        name="deadline"
                                        placeholder="deadline"
                                        required
                                        onChange={handleChange("deadline")}
                                    ></textarea>
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Priority</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="priority"
                                        placeholder="priority"
                                        required
                                        onChange={handleChange("priority")}
                                    />
                                </div>
                                <div className="modal-footer d-block">
                                    <button type="submit" data-bs-dismiss="modal" className="btn btn-warning float-end">Submit</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>


            {/* Edit Modal Form */}
            <div className="modal fade" id="editModalForm" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel">Update</h5>
                            <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleSave}>
                                <div className="mb-3">
                                    <label className="form-label">Title</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="title"
                                        required
                                        value={editFormData.title}
                                        onChange={handleUpdate("title")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Description</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="description"
                                        placeholder="description"
                                        required
                                        value={editFormData.description}
                                        onChange={handleUpdate("description")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Deadline</label>
                                    <textarea
                                        rows="4"
                                        cols="50"
                                        className="form-control"
                                        name="deadline"
                                        placeholder="deadline"
                                        required
                                        value={editFormData.deadline}
                                        onChange={handleUpdate("deadline")}
                                    ></textarea>
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Priority</label>
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="priority"
                                        placeholder="priority"
                                        required
                                        value={editFormData.priority}
                                        onChange={handleUpdate("priority")}
                                    />
                                </div>
                                <div className="modal-footer d-block">
                                    <button type="submit" data-bs-dismiss="modal" className="btn btn-warning float-end">Submit</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>


        </div>
    )
}

export default Table;
