import React, { useState } from 'react';
import EdittableTable from './EdittableTable';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faBars, faPlusCircle, faPenSquare } from '@fortawesome/free-solid-svg-icons'
import toastr from 'toastr';
import "./Table.css";
import { Modal } from 'bootstrap';
import { $ } from 'jquery';
import Button from 'react-bootstrap/Button';
import Col from 'react-bootstrap/Col';
import Form from 'react-bootstrap/Form';
import InputGroup from 'react-bootstrap/InputGroup';
import Row from 'react-bootstrap/Row';

const Table = () => {

    // Toastr settings
    var options = {
        "closeButton": false,
        "debug": false,
        "newestOnTop": false,
        "progressBar": false,
        "positionClass": "toast-bottom-right",
        "preventDuplicates": false,
        "onclick": null,
        "showDuration": "300",
        "hideDuration": "1000",
        "timeOut": "5000",
        "extendedTimeOut": "1000",
        "showEasing": "swing",
        "hideEasing": "linear",
        "showMethod": "fadeIn",
        "hideMethod": "fadeOut"
    }

    // Stores validation
    const [validated, setValidated] = useState(false);

    // Stores the website data in a state hook
    const [datas, setDatas] = useState([]);

    // Stores what should be added to the website data in a
    // different state hook
    const [addPost, setAddPost] = useState({
        title: '',
        description: '',
        deadline: '',
        priority: '',
    });

    // Get unique title reference
    const [editEntry, setEditEntry] = useState(null);

    // Edit the form data
    const [editFormData, setEditFormData] = useState({
        title: '',
        description: '',
        deadline: '',
        priority: '',
    });

    // Receive values from the add modal as it changes
    const handleChange = (input) => (e) => {
        console.log(addPost);
        setAddPost({ ...addPost, [input]: e.target.value });
    };

    // Add data to the data set when the data is submitted
    const handleAddPost = (e) => {
        e.preventDefault();
        const form = e.currentTarget;
        if (form.checkValidity() === false) {
            e.preventDefault();
            e.stopPropagation();
        }

        if (!datas.find((data) => data.title === addPost.title)) {

            const newPost = {
                title: addPost.title,
                description: addPost.description,
                deadline: addPost.deadline,
                priority: addPost.priority
            };

            const newPosts = [...datas, newPost];
            setDatas(newPosts);
            toastr.success('Entry added succesfully', 'Success!', options);
            setAddPost(null);
            setValidated(true);
            $('#addModalForm').modal('hide');
        }
    };


    // Updating Values
    const handleUpdate = (input) => (e) => {
        setEditFormData({ ...editFormData, [input]: e.target.value });
    };

    // Handles putting the original values into the modal
    const handleEdit = (e, data) => {
        e.preventDefault();
        setEditEntry(data.title);
        console.log(data.title);

        const values = {
            title: data.title,
            description: data.description,
            deadline: data.deadline,
            priority: data.priority
        }

        setEditFormData(values);
    };

    // Saves the new updated values back to the original data set but modified
    const handleSave = (e) => {
        e.preventDefault();

        const saveForm = {
            title: editEntry,
            description: editFormData.description,
            deadline: editFormData.deadline,
            priority: editFormData.priority,
        };

        const newForms = [...datas];

        const index = datas.findIndex((data) => data.title === editEntry);
        newForms[index] = saveForm;

        setDatas(newForms);
        setEditEntry(null);
        toastr.success('Entry updated succesfully', 'Success!', options);
    };


    // Deletes the entry from the table
    const handleDelete = (e, data) => {
        e.preventDefault();

        const newPosts = [...datas];
        const index = datas.findIndex((data) => data.title === editEntry);

        newPosts.splice(index, 1);

        setDatas(newPosts);
    };




    return (
        <div>
            <div className="d-flex flex-row header">
                <p className="centered-text header-text">
                    <FontAwesomeIcon icon={faBars} /> FRAMEWORKS
                </p>
                <button type="button" className="me-3 btn btn-primary ml-auto d-block mb-2 centered-text right-aligned-button" data-bs-toggle="modal" data-bs-target="#addModalForm">
                    <FontAwesomeIcon icon={faPlusCircle} /> ADD
                </button>
            </div>
            <table className="table table-bordered border-primary table-responsive">
                <thead>
                    <tr className="centered-text">
                        <th scope="col">Title</th>
                        <th scope="col">Description</th>
                        <th scope="col">Deadline</th>
                        <th scope="col">Priority</th>
                        <th scope="col">Is Complete</th>
                        <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>
                    <EdittableTable
                        datas={datas}
                        handleEdit={handleEdit}
                        handleDelete={handleDelete}
                    />
                </tbody>
            </table>

            {/* Add Modal Form */}
            <div className="modal" id="addModalForm" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel"><FontAwesomeIcon icon={faPlusCircle} /> Add Task</h5>
                        </div>
                        <div className="modal-body">
                            <Form onSubmit={handleAddPost} name="form" id="form" className="needs-validation" noValidate validated={validated}>
                                <Row className="mb-3 input-group has-validation">
                                    <Form.Control
                                        type="text"
                                        className="form-control"
                                        name="title"
                                        placeholder="Title"
                                        required
                                        onChange={handleChange("title")}
                                    />
                                    <Form.Control.Feedback type="invalid">
                                        Title is required!
                                    </Form.Control.Feedback>
                                </Row>
                                <Row className="mb-3">
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="description"
                                        placeholder="Description"
                                        required
                                        onChange={handleChange("description")}
                                    />
                                    <Form.Control.Feedback type="invalid">
                                        Description is required!
                                    </Form.Control.Feedback>
                                </Row>
                                <Row className="mb-3 field-set-add">
                                    <fieldset>
                                        <legend className="legend-border-add">Deadline</legend>
                                        <Form.Control
                                            type="date"
                                            className="form-control button-field-legend-add"
                                            name="deadline"
                                            required
                                            onChange={handleChange("deadline")}
                                        >
                                        </Form.Control>
                                    </fieldset>
                                    <Form.Control.Feedback type="invalid">
                                        Deadline is required!
                                    </Form.Control.Feedback>
                                </Row>

                                <Row className="mb-3">
                                    <div class="form-check form-check-inline">
                                        <Form.Check
                                            type="radio"
                                            inline
                                            label="Low"
                                            name="inlineRadioOptions"
                                            id="inlineRadio1"
                                            value="Low"
                                            onChange={handleChange("priority")}
                                            required
                                        />
                                        <Form.Check
                                            type="radio"
                                            inline
                                            label="Med"
                                            name="inlineRadioOptions"
                                            id="inlineRadio2"
                                            value="Med"
                                            onChange={handleChange("priority")}
                                        />
                                        <Form.Check
                                            type="radio"
                                            inline
                                            label="High"
                                            name="inlineRadioOptions"
                                            id="inlineRadio3"
                                            value="High"
                                            onChange={handleChange("priority")}
                                        />
                                        <Form.Control.Feedback type="invalid">
                                            Priority is required!
                                        </Form.Control.Feedback>
                                    </div>
                                </Row>
                                <div className="modal-footer d-block">
                                    <button type="submit" className="btn btn-primary float-end">Submit</button>
                                    <button type="button" data-bs-dismiss="modal" className="btn btn-warning float-end" id="cancel-btn">Cancel</button>
                                </div>
                            </Form>
                        </div>
                    </div>
                </div>
            </div>


            {/* Edit Modal Form */}
            <div className="modal fade" id="editModalForm" tabIndex="-1" aria-labelledby="exampleModalLabel" aria-hidden="true">
                <div className="modal-dialog">
                    <div className="modal-content">
                        <div className="modal-header">
                            <h5 className="modal-title" id="exampleModalLabel"><FontAwesomeIcon icon={faPenSquare} /> Edit Task</h5>
                        </div>
                        <div className="modal-body">
                            <form onSubmit={handleSave}>
                                <div className="mb-3">
                                    <input
                                        type="text"
                                        className="form-control"
                                        name="description"
                                        placeholder="Description"
                                        required
                                        value={editFormData.description}
                                        onChange={handleUpdate("description")}
                                    />
                                </div>
                                <div className="mb-3">
                                    <label className="form-label">Deadline</label>
                                    <input
                                        type="date"
                                        className="form-control"
                                        name="deadline"
                                        required
                                        value={editFormData.deadline}
                                        onChange={handleUpdate("deadline")}
                                    ></input>
                                </div>
                                <div className="mb-3">
                                    <div class="form-check form-check-inline">
                                        <input class="form-check-input"
                                            type="radio"
                                            name="inlineRadioOptions"
                                            id="inlineRadio1"
                                            value="Low"
                                            onChange={handleUpdate("priority")}
                                        />
                                        <label class="form-check-label" for="inlineRadio1">Low</label>
                                    </div>
                                    <div class="form-check form-check-inline">
                                        <input class="form-check-input"
                                            type="radio"
                                            name="inlineRadioOptions"
                                            id="inlineRadio2"
                                            value="Med"
                                            onChange={handleUpdate("priority")} />
                                        <label class="form-check-label" for="inlineRadio2">Med</label>
                                    </div>
                                    <div class="form-check form-check-inline">
                                        <input
                                            class="form-check-input"
                                            type="radio"
                                            name="inlineRadioOptions"
                                            id="inlineRadio3"
                                            value="High"
                                            onChange={handleUpdate("priority")} />
                                        <label class="form-check-label" for="inlineRadio3">High</label>
                                    </div>
                                </div>
                                <div className="modal-footer d-block">
                                    <button type="submit" data-bs-dismiss="modal" className="btn btn-warning float-end">Submit</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        </div >

    )
}

export default Table;
